mapper.select:
select 

from tpt_account
where ACCOUNT_NUMBER_NAME = #{accountNumberName,jdbcType=VARCHAR}
  and ACCOUNT_NUMBER_TYPE = #{accountNumberType,jdbcType=VARCHAR}

mapper.delete:
delete from tpt_account
where ACCOUNT_NUMBER_NAME = #{accountNumberName,jdbcType=VARCHAR}
  and ACCOUNT_NUMBER_TYPE = #{accountNumberType,jdbcType=VARCHAR}

mapper.update:
update tpt_account
set DESCR = #{descr,jdbcType=VARCHAR},
  CREATE_TIME = #{createTime,jdbcType=TIMESTAMP},
  DELETE_FLAG = #{deleteFlag,jdbcType=CHAR},
  USER_ID = #{userId,jdbcType=CHAR},
  ACCOUNT_NUMBER_ID = #{accountNumberId,jdbcType=CHAR},
  PHOTO = #{photo,jdbcType=CHAR},
  REGISTER_ADDRESS = #{registerAddress,jdbcType=VARCHAR}
where ACCOUNT_NUMBER_NAME = #{accountNumberName,jdbcType=VARCHAR}
  and ACCOUNT_NUMBER_TYPE = #{accountNumberType,jdbcType=VARCHAR}
